mapper.insert:
insert into product_Review values(
to_char(SYSDATE,'yyyyMMdd') || LPAD(product_Review_seq.nextval, 4, 0),
	#{pr_orderNo}, #{pr_csm_id}, #{pr_comment},
	#{pr_gdNo}, #{pr_gdName}, #{pr_star}, #{pr_regDate}
)

